import React, {Component} from "react";
import Personal from "./Personal";
import uniqid from "uniqid";
import Edit from "./Edit";
import NameEtc from "./NameEtc";

class App extends Component {
  constructor() {
    super();

    this.state = {
      task: {
        text: '', 
        id: uniqid()
      },
      tasks: [],
      edit: false,

    };

    this.handleChange = this.handleChange.bind(this);
    this.onSubmitTask = this.onSubmitTask.bind(this);
    this.handleEdit = this.handleEdit.bind(this);
  }

  handleEdit = (e) => {
    e.preventDefault();
    this.setState({
      edit: true,
    })
  }

  handleChange = (e) => {
    this.setState({
      task: {
        text: e.target.value,
        id: this.state.task.id,
      },
    });
  };

  onSubmitTask = (e) => {
    e.preventDefault();
    this.setState({
      tasks: this.state.tasks.concat(this.state.task),
      task: {
        text: '', 
        id: uniqid()
      },
      edit: false,
    });
  };

  render() {
    const { task, tasks } = this.state;

    return (
      <div>
         <NameEtc tasks={tasks} onSubmitTask={this.onSubmitTask} handleChange={this.handleChange} handleEdit={this.handleEdit}/>
        <Personal tasks={tasks} />
        {this.state.edit ? <Edit tasks={tasks} onSubmitTask={this.onSubmitTask} handleChange={this.handleChange} handleEdit={this.handleEdit}/>: null}
      </div>
    );
  }
}

export default App;
